import React, { useState } from "react";
import AddUserForm from "./Components/AddUserForm";
import AddedUser from "./Components/AddedUser";
import * as infoStyle from "./UserInfo.module.css";

const usersList = [
  {
    id: "1",
    name: "Muhib",
    age: 18,
  },
  {
    id: "2",
    name: "Ali",
    age: 19,
  },
  {
    id: "3",
    name: "Hamza",
    age: 20,
  },
];

const App = (props) => {
  const [listOfUsers, updatedListOfusers] = useState(usersList);

  const updatedusersHandler = (addedUser) => {
    console.log(addedUser);
    updatedListOfusers([addedUser, ...listOfUsers]);
  };
  console.log(listOfUsers);

  return (
    <div>
      <AddUserForm addUserInList={updatedusersHandler} />
      <AddedUser renderUsers={listOfUsers} />
      <li>
        <div className={infoStyle["user-info"]}>
          <p className={infoStyle.label}>Username:</p>
          <p>Muhib</p>
          <p className={infoStyle.label}>Age:</p>
          <p>{props.age}</p>
        </div>
      </li>
    </div>
  );
};

export default App;
