  const [age, setAge] = useState("");

  const nameValueHandler = (e) => {
    setName(e.target.value);
  };
  const ageValueHandler = (e) => {
    setAge(e.target.value);
  };
  const submitHandler = (e) => {
    e.preventDefault();
    const dataUser = {
      name: name,
      age: age,
      id: Math.random().toString(),
